INSERT INTO class_features
	(feature, class, description)
VALUES
	('Rage', 'Barbarian', 'In battle, you fight with primal ferocity. On your turn, you can enter a rage as a bonus action.'),
	('Unarmored Defense', 'Barbarian', 'While you are not wearing any armor, your Armor Class equals 10 + your Dexterity modifier + your Constitution modifier. You can use a shield and still gain this benefit.'),
	('Bardic Inspiration', 'Bard', 'You can inspire others through stirring words or music. To do so, you use a bonus action on your turn to choose one creature other than yourself within 60 feet of you who can hear you. That creature gains one Bardic Inspiration die, a d6.'),
	('Jack of All Trades', 'Bard', 'Starting at 2nd level, you can add half your proficiency bonus, rounded down, to any ability check you make that doesn’t already include your proficiency bonus.'),
	('Channel Divinity', 'Cleric', 'At 2nd level, you gain the ability to channel divine energy directly from your deity, using that energy to fuel magical effects. You start with two such effects: Turn Undead and an effect determined by your domain. Some domains grant you additional effects as you advance in levels, as noted in the domain description.'),
	('Spellcasting', 'Cleric', 'As a conduit for divine power, you can cast cleric spells'),
	('Wild Shape', 'Druid', 'Starting at 2nd level, you can use your action to magically assume the shape of a beast that you have seen before. You can use this feature twice. You regain expended uses when you finish a short or long rest.'),
	('Druid Circle', 'Druid', 'At 2nd level, you choose to identify with a circle of druids: the Circle of the Land or the Circle of the Moon, both detailed at the end of the class description. Your choice grants you features at 2nd level and again at 6th, 10th, and 14th level.'),
	('Fighting Style', 'Fighter', 'You adopt a particular style of fighting as your specialty. Choose one of the following options. You can’t take a Fighting Style option more than once, even if you later get to choose again.'),
	('Action Surge', 'Fighter', 'Starting at 2nd level, you can push yourself beyond your normal limits for a moment. On your turn, you can take one additional action on top of your regular action and a possible bonus action.');
